-- Table: public.prueba-- DROP TABLE IF EXISTS public.prueba;CREATE TABLE IF NOT EXISTS public.prueba(    prueba_id character varying(36) COLLATE pg_catalog."default" NOT NULL,    isactive character(1) COLLATE pg_catalog."default" NOT NULL DEFAULT 'Y'::bpchar,    created timestamp without time zone NOT NULL DEFAULT now(),    createdby character varying(36) COLLATE pg_catalog."default" NOT NULL,    updated timestamp without time zone NOT NULL DEFAULT now(),    updatedby character varying(36) COLLATE pg_catalog."default" NOT NULL,    value character varying(40) COLLATE pg_catalog."default" NOT NULL,    name character varying(60) COLLATE pg_catalog."default" NOT NULL,    name2 character varying(60) COLLATE pg_catalog."default",    description character varying(255) COLLATE pg_catalog."default",    location character varying(255) COLLATE pg_catalog."default",    tipo_contribuyente character varying(36) COLLATE pg_catalog."default",    tipo_persona character varying(36) COLLATE pg_catalog."default",    CONSTRAINT prueba_pkey PRIMARY KEY (prueba_id))TABLESPACE pg_default;